<?php	// No direct access to this file	defined('_JEXEC') or die('Restricted access');	// import Joomla controller library	jimport('joomla.application.component.controller');	/**	* SOHODocs Component Controller	*/	class SohoDocsController extends JControllerLegacy{		function display(){			switch(JRequest::getVar('view')){				case 'sohodocs' : $view =& $this->getView('sohodocs', 'html');							       break;				case 'pump'      : $view =& $this->getView('pump', 'html');							       break;				default          : $view =& $this->getView('sohodocs', 'html');			}			$view->display();		}	}